import React, { useState } from 'react';
import axios from 'axios';

const PostTask = () => {
  const [task, setTask] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [level, setLevel] = useState('');
  const [completed, setCompleted] = useState('false');

  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [submittedTasks, setSubmittedTasks] = useState([]);  // New state to store submitted tasks

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setErrorMessage('');
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:5000/task/makeTask', {
        task,
        description,
        date,
        level,
        completed,
      });

      setSuccessMessage('Task submitted successfully!');
      setSubmittedTasks((prevTasks) => [...prevTasks, response.data.task]);  // Add new task to state

      // Clear form fields
      setTask('');
      setDescription('');
      setDate('');
      setLevel('');
      setCompleted('false');
    } catch (error) {
      setErrorMessage('Failed to submit the task. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
    setSuccessMessage('');
    setErrorMessage('');
  };

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-gray-100 to-gray-300 flex flex-col items-center py-10">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-lg mb-8">
        <h1 className="text-3xl font-bold text-blue-600 mb-6 text-center">Post a New Task</h1>

        {successMessage && <p className="text-green-600 bg-green-100 p-4 rounded mb-4">{successMessage}</p>}
        {errorMessage && <p className="text-red-600 bg-red-100 p-4 rounded mb-4">{errorMessage}</p>}

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label htmlFor="task" className="block text-gray-700 font-medium mb-2">Task Name</label>
            <input
              type="text"
              id="task"
              value={task}
              onChange={handleInputChange(setTask)}
              placeholder="Enter task name"
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="description" className="block text-gray-700 font-medium mb-2">Description</label>
            <textarea
              id="description"
              value={description}
              onChange={handleInputChange(setDescription)}
              placeholder="Enter task description"
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              rows="4"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="date" className="block text-gray-700 font-medium mb-2">Due Date</label>
            <input
              type="datetime-local"
              id="date"
              value={date}
              onChange={handleInputChange(setDate)}
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="mb-4">
            <label htmlFor="level" className="block text-gray-700 font-medium mb-2">Level (1-5)</label>
            <input
              type="number"
              id="level"
              value={level}
              onChange={handleInputChange(setLevel)}
              min="1"
              max="5"
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="mb-6">
            <label htmlFor="completed" className="block text-gray-700 font-medium mb-2">Completed</label>
            <select
              id="completed"
              value={completed}
              onChange={handleInputChange(setCompleted)}
              className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="false">No</option>
              <option value="true">Yes</option>
            </select>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition"
            disabled={loading}
          >
            {loading ? 'Submitting...' : 'Submit Task'}
          </button>
        </form>
      </div>

      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-4xl">
        <h2 className="text-2xl font-bold text-gray-800 mb-6">Submitted Tasks</h2>
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-3 border border-gray-300">Task Name</th>
              <th className="p-3 border border-gray-300">Description</th>
              <th className="p-3 border border-gray-300">Due Date</th>
              <th className="p-3 border border-gray-300">Level</th>
              <th className="p-3 border border-gray-300">Completed</th>
            </tr>
          </thead>
          <tbody>
            {submittedTasks.map((task, index) => (
              <tr key={index} className="even:bg-gray-100">
                <td className="p-3 border border-gray-300">{task.task}</td>
                <td className="p-3 border border-gray-300">{task.description}</td>
                <td className="p-3 border border-gray-300">{new Date(task.date).toLocaleString()}</td>
                <td className="p-3 border border-gray-300">{task.level}</td>
                <td className="p-3 border border-gray-300">{task.completed === 'true' ? 'Yes' : 'No'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PostTask;
