import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PostProject from './PostProject';
import GetProjects from './GetProjects';
import Student from './Student';
import CreateUser from './CreateUser';
import GetAlert from './GetAlert';
import AdmAlert from "./AdmAlert";

const AdminDashboard = () => {
    axios.defaults.withCredentials = true;
    const [adminEmail, setAdminEmail] = useState('');
    const [studentCount, setStudentCount] = useState(0);
    const [error, setError] = useState("");
    const [weeklyProjects, setWeeklyProjects] = useState(0);
    const [projects, setProjects] = useState(0);
    
    useEffect(() => {
        const fetchAdminData = async () => {
            try {
                const response = await axios.get("http://localhost:5000/admin/adminDetails");
                setAdminEmail(response.data.email);
            } catch (error) {
                console.error("Failed to fetch admin details", error);
            }
        };
        fetchAdminData();
    }, []);
    
    // Logout handler
    const handleLogout = async () => {
        try {
            await axios.post("http://localhost:5000/admin/adminLogout");
            window.location.href = '/';
        } catch (error) {
            console.error("Logout failed", error);
        }
    };
    
    return (
        <div className="min-h-screen bg-gray-50 flex flex-col items-center justify-center">
            {/* Display Admin Email */}
            <div className="mb-4 p-4 bg-white shadow-md rounded-lg">
                <h2 className="text-xl font-semibold">Logged in as:</h2>
                <p className="text-blue-600 font-medium">{adminEmail || 'Loading...'}</p>
            </div>
            
            {/* Admin Panel */}
            <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg p-6">
                <h2 className="text-2xl font-bold mb-4">Admin Panel</h2>
                <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded-lg">Logout</button>
            </div>
        </div>
    );
};

export default AdminDashboard;
