import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GetProjects = () => {
    const [projects, setProjects] = useState([]);

    const fetchProjects = async () => {
        try {
            const response = await axios.get("http://localhost:5000/projects/getAllprojects");
            setProjects(response.data);
        } catch (error) {
            console.error(error.message);
        }
    };

    const deleteProject = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/projects/delete/${id}`);
            setProjects(projects.filter(project => project.id !== id));
        } catch (error) {
            console.error("Error deleting project:", error);
        }
    };

    const updateProject = (id) => {
        console.log("Update project", id);
        // You can add further logic for updating a project
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    return (
        <div className="flex">
            {/* Sidebar */}
            <div className="fixed w-64 h-screen bg-blue-600 text-white shadow-lg p-4 overflow-y-auto">
                <h2 className="text-xl font-bold mb-6">Admin Panel</h2>
                <nav>
                    <ul className="space-y-2">
                        <li><button className="w-full text-left px-4 py-2 rounded-lg hover:bg-blue-700">Dashboard</button></li>
                        <li><button className="w-full text-left px-4 py-2 rounded-lg hover:bg-blue-700">Manage Students</button></li>
                    </ul>
                </nav>
            </div>

            {/* Main Content */}
            <div className="ml-64 p-6 bg-gray-100 min-h-screen w-full overflow-y-auto">
                <h2 className="text-2xl font-bold text-center mb-6">Projects</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {projects.map((project, index) => (
                        <div
                            key={index}
                            className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow"
                        >
                            <h3 className="text-lg font-semibold text-gray-800">{project.title}</h3>
                            <p className="text-gray-600 mt-2">{project.Description}</p>
                            <p className="text-sm text-gray-500 mt-1">Reference: {project.reference}</p>
                            <p className="text-sm text-gray-500">Year: {project.year}</p>
                            <div className="flex justify-between mt-4">
                                <button
                                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                                    onClick={() => deleteProject(project.id)}
                                >
                                    Delete
                                </button>
                                <button
                                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                                    onClick={() => updateProject(project.id)}
                                >
                                    Update
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default GetProjects;
